import React, { useState, useEffect } from "react";

const DonnePrendPhase = ({ players, remainingDeck, playerCards, updateGorgees, endDonnePrendPhase }) => {
  const [currentCard, setCurrentCard] = useState(null);
  const [cardRevealed, setCardRevealed] = useState(false);
  const [message, setMessage] = useState("");
  const [playersWithCard, setPlayersWithCard] = useState([]);
  const [currentGiverIndex, setCurrentGiverIndex] = useState(0);
  const [isHardcoreMode, setIsHardcoreMode] = useState(false); // Pour gérer le mode hardcore

  // Fonction pour tirer une carte du deck restant
  const drawCard = () => {
    if (!remainingDeck || remainingDeck.length === 0) {
      setMessage("Le deck est vide, la phase est terminée !");
      return;
    }

    // Tirer une nouvelle carte
    const newDeck = [...remainingDeck];
    const card = newDeck.pop();
    setCurrentCard(card); // Mettre à jour la carte actuelle
    setCardRevealed(true);
    setMessage(`Carte tirée : ${card.value} de ${card.suit}`);

    // Vérifier les cartes des joueurs
    if (!playerCards || playerCards.length === 0) {
      setMessage("Erreur : Les cartes des joueurs ne sont pas disponibles.");
      return;
    }

    // Trouver les joueurs ayant cette carte
    const playersWithThisCard = players
      .map((player, index) => ({
        playerName: player,
        playerIndex: index,
        hasCard: playerCards[index]?.some(
          (playerCard) => playerCard?.value === card?.value && playerCard?.suit === card?.suit
        ),
      }))
      .filter((playerData) => playerData.hasCard);

    if (playersWithThisCard.length === 0) {
      // Mode Hardcore : tirer une nouvelle carte si personne n'a la carte
      setMessage("Mode Hardcore ! Aucune correspondance, nouvelle carte !");
      setIsHardcoreMode(true);
      setTimeout(() => {
        setIsHardcoreMode(false);
        drawCard(); // Tirer une nouvelle carte après un court délai
      }, 2000);
    } else {
      setPlayersWithCard(playersWithThisCard.map((p) => p.playerIndex));
      setCurrentGiverIndex(0); // Commence par le premier joueur qui doit donner
    }
  };

  // Fonction pour distribuer 1 gorgée
  const handleDistributeGorgee = (toPlayer) => {
    updateGorgees(toPlayer, 1);
    const currentGiver = players[playersWithCard[currentGiverIndex]];
    setMessage(`${currentGiver} a distribué 1 gorgée à ${players[toPlayer]}.`);

    // Passer au prochain joueur qui doit distribuer ou terminer
    if (currentGiverIndex < playersWithCard.length - 1) {
      setCurrentGiverIndex(currentGiverIndex + 1);
    } else {
      setMessage("Distribution terminée. Passer au joueur suivant.");
      setTimeout(endDonnePrendPhase, 2000); // Attendre avant de passer à l'étape suivante
    }
  };

  useEffect(() => {
    if (!isHardcoreMode && !cardRevealed) {
      drawCard(); // Assurer qu'on ne tire pas de carte en mode hardcore
    }
  }, []);

  return (
    <div className="donne-prend-phase">
      <h1>Phase Donne/Prend</h1>
      <p>{message}</p>
      {playersWithCard.length > 0 && currentCard && (
        <div>
          {playersWithCard[currentGiverIndex] !== undefined && (
            <>
              <h3>{players[playersWithCard[currentGiverIndex]]}, distribuez vos gorgées</h3>
              {players.map((name, index) =>
                index !== playersWithCard[currentGiverIndex] ? (
                  <button key={index} onClick={() => handleDistributeGorgee(index)}>
                    Donner 1 gorgée à {name}
                  </button>
                ) : null
              )}
            </>
          )}
        </div>
      )}
    </div>
  );
};

export default DonnePrendPhase;
