import React, { useState } from 'react';
import Card from '../Card/Card';

function Round1({ player, onAction, deck, onSaveCard }) {
  const [currentCard, setCurrentCard] = useState(null);
  const [guess, setGuess] = useState('');
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleSubmit = () => {
    const card = deck.pop(); // Tire une carte du deck
    setCurrentCard(card);

    const isCorrect = (guess === 'red' && (card.suit === 'hearts' || card.suit === 'diamonds')) ||
                      (guess === 'black' && (card.suit === 'spades' || card.suit === 'clubs'));

    setIsSubmitted(true);
    onSaveCard(player, card); // Sauvegarde la carte tirée pour ce joueur
    onAction(isCorrect ? 'distribute' : 'drink', 1);
  };

  const handleNextPlayer = () => {
    setIsSubmitted(false);
    setGuess('');
    onAction('next');
  };

  return (
    <div className="round1">
      <h2>{player}, devine si la carte est rouge ou noire :</h2>
      <button disabled={isSubmitted} onClick={() => setGuess('red')}>Rouge</button>
      <button disabled={isSubmitted} onClick={() => setGuess('black')}>Noir</button>

      {isSubmitted && (
        <div>
          <Card card={currentCard} />
          <p>{guess === 'red' ? 'Rouge' : 'Noir'} était {currentCard && (isCorrect ? 'correct' : 'incorrect')}.</p>
          <button onClick={handleNextPlayer}>Joueur suivant</button>
        </div>
      )}

      {!isSubmitted && (
        <button className="submit-btn" onClick={handleSubmit} disabled={!guess}>
          Valider
        </button>
      )}
    </div>
  );
}

export default Round1;
