import React, { useState } from 'react';
import './app.css';

function App() {
  const [numPlayers, setNumPlayers] = useState(2); // Nombre de joueurs
  const [playerNames, setPlayerNames] = useState(Array(numPlayers).fill('')); // Noms des joueurs
  const [startGame, setStartGame] = useState(false); // Démarrage du jeu
  const [currentPlayer, setCurrentPlayer] = useState(0); // Suivi du joueur actuel
  const [roundNumber, setRoundNumber] = useState(1); // Numéro du tour (1 à 4)
  const [turnNumber, setTurnNumber] = useState(1); // Tour de chaque joueur (chaque joueur fait son tour 1, puis passe à 2...)
  const [message, setMessage] = useState(''); // Messages de feedback
  const [currentCard, setCurrentCard] = useState(null); // Carte courante
  const [playerCards, setPlayerCards] = useState(Array(numPlayers).fill([])); // Cartes tirées par chaque joueur
  const [gorgéesDistribuées, setGorgeesDistribuees] = useState(Array(numPlayers).fill(0)); // Gorgées distribuées
  const [gorgéesReçues, setGorgeesRecues] = useState(Array(numPlayers).fill(0)); // Gorgées reçues
  const [showDistribution, setShowDistribution] = useState(false); // Affichage de la distribution des gorgées
  const [cardRevealed, setCardRevealed] = useState(false); // Carte révélée après la devinette

  const suits = ['cœur', 'carreau', 'pique', 'trèfle']; // Formes de carte

  // Génère une carte aléatoire (valeur et couleur)
  const generateCard = () => {
    const value = Math.floor(Math.random() * 13) + 1; // Carte de 1 à 13
    const suit = suits[Math.floor(Math.random() * 4)];
    return { value, suit };
  };

  // Gestion du nombre de joueurs
  const handleNumPlayersChange = (e) => {
    const value = parseInt(e.target.value);
    setNumPlayers(value);
    setPlayerNames(Array(value).fill(''));
    setPlayerCards(Array(value).fill([])); // Initialiser les cartes pour chaque joueur
  };

  // Gestion du changement de pseudo
  const handlePlayerNameChange = (e, index) => {
    const newPlayerNames = [...playerNames];
    newPlayerNames[index] = e.target.value;
    setPlayerNames(newPlayerNames);
  };

  // Démarrer le jeu après avoir saisi les pseudos
  const handleStartGame = () => {
    if (playerNames.every(name => name.trim() !== '')) {
      setStartGame(true);
      setMessage(`${playerNames[currentPlayer]} commence le tour 1 : Rouge ou Noir.`);
      const card = generateCard();
      setCurrentCard(card);
      setCardRevealed(false); // Cacher la carte tant que la devinette n'est pas faite
    } else {
      alert('Veuillez remplir tous les noms des joueurs.');
    }
  };

  // Fonction pour gérer la devinette du joueur selon le tour actuel
  const handlePlayerGuess = (guess) => {
    setCardRevealed(true); // Révéler la carte après la devinette

    switch (roundNumber) {
      case 1:
        handleColorGuess(guess);
        break;
      case 2:
        handleComparisonGuess(guess);
        break;
      case 3:
        handleInsideOutsideGuess(guess);
        break;
      case 4:
        handleSuitGuess(guess);
        break;
      default:
        break;
    }
  };

  // Tour 1 : Rouge ou Noir
  const handleColorGuess = (guess) => {
    const isRed = currentCard.suit === 'cœur' || currentCard.suit === 'carreau';
    if ((guess === 'rouge' && isRed) || (guess === 'noir' && !isRed)) {
      setMessage(`${playerNames[currentPlayer]} a deviné correctement et peut distribuer 1 gorgée.`);
      setShowDistribution(true); // Afficher la distribution des gorgées
    } else {
      setMessage(`${playerNames[currentPlayer]} a perdu et boit 1 gorgée.`);
      let newGorgeesRecues = [...gorgéesReçues];
      newGorgeesRecues[currentPlayer] += 1; // Boit une gorgée
      setGorgeesRecues(newGorgeesRecues);
      nextTurn(); // Passer au joueur suivant après avoir bu
    }
  };

  // Tour 2 : Supérieure, Inférieure ou Égale
  const handleComparisonGuess = (guess) => {
    const previousCard = playerCards[currentPlayer][0]; // Carte du premier tour
    const comparison = currentCard.value - previousCard.value;

    if ((guess === 'supérieure' && comparison > 0) ||
        (guess === 'inférieure' && comparison < 0) ||
        (guess === 'égale' && comparison === 0)) {
      setMessage(`${playerNames[currentPlayer]} a deviné correctement et peut distribuer 2 gorgées.`);
      setShowDistribution(true);
    } else {
      setMessage(`${playerNames[currentPlayer]} a perdu et boit 2 gorgées.`);
      let newGorgeesRecues = [...gorgéesReçues];
      newGorgeesRecues[currentPlayer] += 2;
      setGorgeesRecues(newGorgeesRecues);
      nextTurn();
    }
  };

  // Tour 3 : À l'intérieur ou à l'extérieur
  const handleInsideOutsideGuess = (guess) => {
    const firstCard = playerCards[currentPlayer][0];
    const secondCard = playerCards[currentPlayer][1];
    const isInside = currentCard.value > Math.min(firstCard.value, secondCard.value) && currentCard.value < Math.max(firstCard.value, secondCard.value);

    if ((guess === 'intérieur' && isInside) || (guess === 'extérieur' && !isInside)) {
      setMessage(`${playerNames[currentPlayer]} a deviné correctement et peut distribuer 3 gorgées.`);
      setShowDistribution(true);
    } else {
      setMessage(`${playerNames[currentPlayer]} a perdu et boit 3 gorgées.`);
      let newGorgeesRecues = [...gorgéesReçues];
      newGorgeesRecues[currentPlayer] += 3;
      setGorgeesRecues(newGorgeesRecues);
      nextTurn();
    }
  };

  // Tour 4 : Forme
  const handleSuitGuess = (guess) => {
    if (guess === currentCard.suit) {
      setMessage(`${playerNames[currentPlayer]} a deviné correctement et peut distribuer 4 gorgées.`);
      setShowDistribution(true);
    } else {
      setMessage(`${playerNames[currentPlayer]} a perdu et boit 4 gorgées.`);
      let newGorgeesRecues = [...gorgéesReçues];
      newGorgeesRecues[currentPlayer] += 4;
      setGorgeesRecues(newGorgeesRecues);
      nextTurn();
    }
  };

  // Fonction pour distribuer des gorgées
  const distributeGorgees = (toPlayer, amount) => {
    let newGorgeesDistribuees = [...gorgéesDistribuées];
    newGorgeesDistribuees[currentPlayer] += amount;

    let newGorgeesRecues = [...gorgéesReçues];
    newGorgeesRecues[toPlayer] += amount;

    setGorgeesDistribuees(newGorgeesDistribuees);
    setGorgeesRecues(newGorgeesRecues);
    setShowDistribution(false);
    nextTurn();
  };

  // Passer au tour suivant pour un joueur
  const nextTurn = () => {
    let nextPlayer = (currentPlayer + 1) % numPlayers;

    // Ajouter la carte tirée à l'historique du joueur actuel
    let newPlayerCards = [...playerCards];
    newPlayerCards[currentPlayer] = [...newPlayerCards[currentPlayer], currentCard];
    setPlayerCards(newPlayerCards);

    // Si tous les joueurs ont fait le même tour, passer au tour suivant
    if (nextPlayer === 0) {
      setTurnNumber(turnNumber + 1); // Passer au tour suivant pour tous les joueurs
      if (turnNumber === 4) {
        setTurnNumber(1); // Si on a fait les 4 tours, recommencer
      }
    }

    // Générer une nouvelle carte pour le joueur suivant
    const newCard = generateCard();
    setCurrentCard(newCard);
    setCardRevealed(false); // Cacher la nouvelle carte tant que la devinette n'est pas faite

    setCurrentPlayer(nextPlayer);
    setMessage(`${playerNames[nextPlayer]}, à toi de jouer pour le tour ${roundNumber}.`);
  };

  // Récapitulatif à la fin
  const showRecap = () => {
    return playerNames.map((name, index) => (
      <p key={index}>
        {name} a distribué {gorgéesDistribuées[index]} gorgées et a bu {gorgéesReçues[index]} gorgées.
      </p>
    ));
  };

  if (startGame) {
    return (
      <div className="game">
        <h2>{message}</h2>

        {cardRevealed ? (
          <p>Carte révélée : {currentCard?.value} de {currentCard?.suit}</p>
        ) : null}

        {playerCards[currentPlayer].length > 0 && (
          <div>
            <h3>Cartes tirées par {playerNames[currentPlayer]}</h3>
            {playerCards[currentPlayer].map((card, index) => (
              <p key={index}>
                {card.value} de {card.suit}
              </p>
            ))}
          </div>
        )}

        {turnNumber === 1 && !showDistribution && !cardRevealed && (
          <div>
            <h3>Devinez si la carte est rouge ou noire</h3>
            <button onClick={() => handlePlayerGuess('rouge')}>Rouge</button>
            <button onClick={() => handlePlayerGuess('noir')}>Noir</button>
          </div>
        )}

        {turnNumber === 2 && !showDistribution && !cardRevealed && (
          <div>
            <h3>Devinez si la carte est supérieure, inférieure ou égale à la première</h3>
            <button onClick={() => handlePlayerGuess('supérieure')}>Supérieure</button>
            <button onClick={() => handlePlayerGuess('inférieure')}>Inférieure</button>
            <button onClick={() => handlePlayerGuess('égale')}>Égale</button>
          </div>
        )}

        {turnNumber === 3 && !showDistribution && !cardRevealed && (
          <div>
            <h3>Devinez si la carte est à l'intérieur ou à l'extérieur des cartes précédentes</h3>
            <button onClick={() => handlePlayerGuess('intérieur')}>À l'intérieur</button>
            <button onClick={() => handlePlayerGuess('extérieur')}>À l'extérieur</button>
          </div>
        )}

        {turnNumber === 4 && !showDistribution && !cardRevealed && (
          <div>
            <h3>Devinez la forme de la carte</h3>
            <button onClick={() => handlePlayerGuess('cœur')}>Cœur</button>
            <button onClick={() => handlePlayerGuess('carreau')}>Carreau</button>
            <button onClick={() => handlePlayerGuess('pique')}>Pique</button>
            <button onClick={() => handlePlayerGuess('trèfle')}>Trèfle</button>
          </div>
        )}

        {showDistribution ? (
          <div>
            <h3>À qui distribuer ?</h3>
            {playerNames.map((name, index) => (
              index !== currentPlayer && (
                <button key={index} onClick={() => distributeGorgees(index, turnNumber)}>
                  {name}
                </button>
              )
            ))}
          </div>
        ) : null}

        {turnNumber === 4 && (
          <div>
            <h2>Récapitulatif</h2>
            {showRecap()}
          </div>
        )}
      </div>
    );
  }

  return (
    <div className="App">
      <h1 className="title">L'Ardechoise</h1>
      {/* Sélecteur de nombre de joueurs */}
      <div className="player-selection">
        <label htmlFor="numPlayers">Nombre de joueurs :</label>
        <select id="numPlayers" value={numPlayers} onChange={handleNumPlayersChange}>
          {[...Array(9).keys()].map((num) => (
            <option key={num + 2} value={num + 2}>
              {num + 2}
            </option>
          ))}
        </select>

        {/* Champs de saisie pour les noms des joueurs */}
        <div className="player-names">
          {playerNames.map((name, index) => (
            <input
              key={index}
              type="text"
              placeholder={`Joueur ${index + 1}`}
              value={name}
              onChange={(e) => handlePlayerNameChange(e, index)}
            />
          ))}
        </div>
      </div>

      {/* Bouton pour lancer le jeu */}
      <button className="start-game-btn" onClick={handleStartGame}>Lancer le jeu</button>
    </div>
  );
}

export default App;
