import React, { useState } from "react";
import Card from "../Card/Card";

function Round2({ player, onAction, deck, previousCard }) {
  const [currentCard, setCurrentCard] = useState(null);
  const [guess, setGuess] = useState("");
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isCorrect, setIsCorrect] = useState(null);

  const handleSubmit = () => {
    const card = deck.pop(); // Tire une nouvelle carte
    setCurrentCard(card);

    const correct =
      (guess === "higher" && card.value > previousCard.value) ||
      (guess === "lower" && card.value < previousCard.value) ||
      (guess === "equal" && card.value === previousCard.value);

    setIsCorrect(correct);
    setIsSubmitted(true);
    onAction(correct ? "distribute" : "drink", 2); // 2 gorgées pour ce round
  };

  const handleNextPlayer = () => {
    setIsSubmitted(false);
    setGuess("");
    onAction("next");
  };

  return (
    <div className="round2">
      <h2>
        {player}, devine si la prochaine carte est supérieure, inférieure ou
        égale :
      </h2>
      <button disabled={isSubmitted} onClick={() => setGuess("higher")}>
        Supérieure
      </button>
      <button disabled={isSubmitted} onClick={() => setGuess("lower")}>
        Inférieure
      </button>
      <button disabled={isSubmitted} onClick={() => setGuess("equal")}>
        Égal
      </button>

      {isSubmitted && (
        <div>
          <Card card={currentCard} />
          <p>
            La carte précédente était {previousCard.value}. La nouvelle carte
            est {currentCard.value}. {isCorrect ? "Correct !" : "Incorrect !"}
          </p>
          <button onClick={handleNextPlayer}>Joueur suivant</button>
        </div>
      )}

      {!isSubmitted && (
        <button className="submit-btn" onClick={handleSubmit} disabled={!guess}>
          Valider
        </button>
      )}
    </div>
  );
}

export default Round2;
