import React, { useState } from "react";
import Rules from "./components/rules/Rules";
import Game from "./components/Game/Game";
import "./app.css";

function App() {
  const [numPlayers, setNumPlayers] = useState(2);
  const [playerNames, setPlayerNames] = useState(Array(numPlayers).fill(""));
  const [showRules, setShowRules] = useState(false);
  const [startGame, setStartGame] = useState(false);

  const handleNumPlayersChange = (e) => {
    const value = parseInt(e.target.value);
    setNumPlayers(value);
    setPlayerNames(Array(value).fill(""));
  };

  const handlePlayerNameChange = (e, index) => {
    const newPlayerNames = [...playerNames];
    newPlayerNames[index] = e.target.value;
    setPlayerNames(newPlayerNames);
  };

  const handleViewRules = () => {
    setShowRules(true);
  };

  const closeRules = () => {
    setShowRules(false);
  };

  const handleStartGame = () => {
    if (playerNames.every((name) => name.trim() !== "")) {
      setStartGame(true);
    } else {
      alert("Veuillez remplir tous les noms des joueurs.");
    }
  };

  if (startGame) {
    return <Game players={playerNames} />;
  }

  return (
    <div className="App">
      <h1 className="title">L' Ardechoise</h1>
      <h1 className="title2">Gays des bois s'abstenir</h1>

      <div className="player-selection">
        <label htmlFor="numPlayers">Nombre de joueurs :</label>
        <select
          id="numPlayers"
          value={numPlayers}
          onChange={handleNumPlayersChange}
        >
          {[...Array(9).keys()].map((num) => (
            <option key={num + 2} value={num + 2}>
              {num + 2}
            </option>
          ))}
        </select>

        <div className="player-names">
          {playerNames.map((name, index) => (
            <input
              key={index}
              type="text"
              placeholder={`Joueur ${index + 1}`}
              value={name}
              onChange={(e) => handlePlayerNameChange(e, index)}
            />
          ))}
        </div>
      </div>

      <button className="view-rules-btn" onClick={handleViewRules}>
        Voir les r√®gles
      </button>
      <button className="start-game-btn" onClick={handleStartGame}>
        Lancer le jeu
      </button>

      {showRules && <Rules onClose={closeRules} />}
    </div>
  );
}

export default App;
