import React, { useState } from 'react';
import Card from '../Card/Card'; // Assurez-vous que le chemin vers le composant Card est correct

function Round3({ player, deck, previousCards, onAction }) {
  const [currentCard, setCurrentCard] = useState(null);
  const [guess, setGuess] = useState('');
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isCorrect, setIsCorrect] = useState(null);

  const card1 = previousCards[0];
  const card2 = previousCards[1];

  const handleSubmit = () => {
    const card = deck.pop(); // Tire une nouvelle carte
    setCurrentCard(card);

    const minValue = Math.min(card1.value, card2.value);
    const maxValue = Math.max(card1.value, card2.value);

    const correct =
      (guess === 'inside' && card.value > minValue && card.value < maxValue) ||
      (guess === 'outside' && (card.value < minValue || card.value > maxValue));

    setIsCorrect(correct);
    setIsSubmitted(true);
    onAction(correct ? 'distribute' : 'drink', 3); // 3 gorgées pour ce round
  };

  const handleNextPlayer = () => {
    setIsSubmitted(false);
    setGuess('');
    onAction('next'); // Passe au joueur suivant
  };

  return (
    <div className="round3">
      <h2>{player}, devine si la carte est à l'intérieur ou à l'extérieur des valeurs des deux cartes précédentes :</h2>

      {/* Affiche les deux cartes des rounds précédents */}
      <Card card={card1} />
      <Card card={card2} />

      <button disabled={isSubmitted} onClick={() => setGuess('inside')}>
        Intérieur
      </button>
      <button disabled={isSubmitted} onClick={() => setGuess('outside')}>
        Extérieur
      </button>

      {isSubmitted && (
        <div>
          <Card card={currentCard} />
          <p>
            La nouvelle carte est {currentCard.value}. {isCorrect ? "Correct !" : "Incorrect !"}
          </p>
          <button onClick={handleNextPlayer}>Joueur suivant</button>
        </div>
      )}

      {!isSubmitted && (
        <button className="submit-btn" onClick={handleSubmit} disabled={!guess}>
          Valider
        </button>
      )}
    </div>
  );
}

export default Round3;
