import React, { useState } from 'react';
import './app.css';
import WowGuy from './assets/images/WowGuy.webp';

function App() {
  const [startGame, setStartGame] = useState(false);
  const [showRecap, setShowRecap] = useState(false);
  const [donnePrendPhase, setDonnePrendPhase] = useState(false);
  const [showIntermediatePage, setShowIntermediatePage] = useState(false);

  // Phase 1: Le récap apparaît après la première phase
  const handleContinueToRecap = () => {
    setShowIntermediatePage(false);  // Cache la page intermédiaire
    setShowRecap(true);  // Affiche le récap provisoire
  };

  // Transition vers la phase Donne/Prend
  const startDonnePrendPhase = () => {
    setShowRecap(false);  // Cache le récap
    setDonnePrendPhase(true);  // Affiche la phase Donne/Prend
  };

  return (
    <div className="App">
      {startGame ? (
        <div className="game">
          {showRecap ? (
            <div>
              <h2>Récapitulatif provisoire</h2>
              {/* Ajoute ici ton contenu de récap */}
              <button onClick={startDonnePrendPhase}>On passe à la suite !</button>
            </div>
          ) : showIntermediatePage ? (
            <div>
              <h2>La première phase de jeu est terminée !</h2>
              <p>Vous pouvez reposer vos foies... Mais pas trop longtemps car la suite arrive !</p>
              <button onClick={handleContinueToRecap}>Passer au récap provisoire avant la suite</button>
            </div>
          ) : donnePrendPhase ? (
            <div>
              <h2>Phase Donne/Prend</h2>
              {/* Ton contenu pour la phase Donne/Prend ici */}
              <p>Les cartes vont apparaître ici</p>
            </div>
          ) : (
            <div>
              <h2>Première phase du jeu</h2>
              {/* Contenu de la première phase */}
              <p>Rouge ou Noir...</p>
            </div>
          )}
        </div>
      ) : (
        <div className="player-setup">
          <h1 className="title">L'Ardechoise</h1>
          <button onClick={() => setStartGame(true)}>Lancer le jeu</button>
        </div>
      )}
    </div>
  );
}

export default App;
