import React, { useState } from 'react';
import './app.css';

function App() {
  const [numPlayers, setNumPlayers] = useState(2); // Nombre de joueurs
  const [playerNames, setPlayerNames] = useState(Array(numPlayers).fill('')); // Noms des joueurs
  const [startGame, setStartGame] = useState(false); // Démarrage du jeu
  const [currentTurn, setCurrentTurn] = useState(0); // Suivi des tours
  const [currentPlayer, setCurrentPlayer] = useState(0); // Suivi du joueur actuel
  const [message, setMessage] = useState(''); // Messages de feedback
  const [showRules, setShowRules] = useState(false); // Affichage des règles

  const totalTurns = 4; // 4 tours dans la première phase

  // Gestion du nombre de joueurs
  const handleNumPlayersChange = (e) => {
    const value = parseInt(e.target.value);
    setNumPlayers(value);
    setPlayerNames(Array(value).fill(''));
  };

  // Gestion du changement de pseudo
  const handlePlayerNameChange = (e, index) => {
    const newPlayerNames = [...playerNames];
    newPlayerNames[index] = e.target.value;
    setPlayerNames(newPlayerNames);
  };

  // Démarrer le jeu après avoir saisi les pseudos
  const handleStartGame = () => {
    if (playerNames.every(name => name.trim() !== '')) {
      setStartGame(true);
      setMessage(`Le jeu commence avec ${playerNames[currentPlayer]}.`);
    } else {
      alert('Veuillez remplir tous les noms des joueurs.');
    }
  };

  // Fonction pour avancer au tour suivant
  const nextTurn = () => {
    let nextPlayer = (currentPlayer + 1) % numPlayers;
    let nextTurn = currentTurn + 1;

    if (nextTurn >= totalTurns) {
      setMessage('Fin de la première phase !');
      // Passage à la deuxième phase ou autre logique ici
    } else {
      setCurrentTurn(nextTurn);
      setCurrentPlayer(nextPlayer);
      setMessage(`${playerNames[nextPlayer]}, à ton tour !`);
    }
  };

  // Affichage des règles
  const toggleRules = () => {
    setShowRules(!showRules);
  };

  if (startGame) {
    return (
      <div className="game">
        <h2>{message}</h2>
        <button onClick={nextTurn}>Tour Suivant</button>
      </div>
    );
  }

  return (
    <div className="App">
      <h1 className="title">L'Ardechoise</h1>

      {/* Bouton pour afficher/masquer les règles */}
      <button className="rules-btn" onClick={toggleRules}>
        {showRules ? 'Cacher les règles' : 'Afficher les règles'}
      </button>

      {/* Section des règles */}
      {showRules && (
        <div className="rules">
          <h2>Règles du jeu</h2>
          <p>Phase 1 : 4 tours</p>
          <ul>
            <li>Tour 1 : Deviner rouge ou noir.</li>
            <li>Tour 2 : Deviner si la nouvelle carte est supérieure, inférieure ou égale.</li>
            <li>Tour 3 : Deviner si la carte est à l'intérieur ou à l'extérieur des cartes précédentes.</li>
            <li>Tour 4 : Deviner la forme de la carte.</li>
          </ul>
          <p>Phase 2 : Donne/Prend</p>
          <p>Deux colonnes de 4 cartes... etc.</p>
        </div>
      )}

      {/* Sélecteur de nombre de joueurs */}
      <div className="player-selection">
        <label htmlFor="numPlayers">Nombre de joueurs :</label>
        <select id="numPlayers" value={numPlayers} onChange={handleNumPlayersChange}>
          {[...Array(9).keys()].map((num) => (
            <option key={num + 2} value={num + 2}>
              {num + 2}
            </option>
          ))}
        </select>

        {/* Champs de saisie pour les noms des joueurs */}
        <div className="player-names">
          {playerNames.map((name, index) => (
            <input
              key={index}
              type="text"
              placeholder={`Joueur ${index + 1}`}
              value={name}
              onChange={(e) => handlePlayerNameChange(e, index)}
            />
          ))}
        </div>
      </div>

      {/* Bouton pour lancer le jeu */}
      <button className="start-game-btn" onClick={handleStartGame}>Lancer le jeu</button>
    </div>
  );
}

export default App;
