import React, { useState } from 'react';

function DonnePrendPhase({
  playerNames,
  gorgeesDistribuees,
  gorgeesRecues,
  setGorgeesDistribuees,
  setGorgeesRecues,
}) {
  const [currentStep, setCurrentStep] = useState(0); // Pour suivre les étapes
  const [cardRevealed, setCardRevealed] = useState(false); // Pour gérer la révélation de la carte

  const steps = [
    { label: "Je donne 1 gorgée", action: "donner" },
    { label: "Je prends 1 gorgée", action: "prendre" },
    { label: "Je donne 2 gorgées", action: "donner" },
    { label: "Je prends 2 gorgées", action: "prendre" },
    { label: "Je donne 3 gorgées", action: "donner" },
    { label: "Je prends 3 gorgées", action: "prendre" },
    { label: "Je donne 4 gorgées", action: "donner" },
    { label: "Je prends 4 gorgées", action: "prendre" },
    { label: "Cul Sec !", action: "cul-sec" },
  ];

  const revealCard = () => {
    setCardRevealed(true);  // Carte révélée
    const step = steps[currentStep];
    alert(`Carte révélée : ${step.label}`);
  };

  const handleNext = () => {
    if (currentStep < steps.length - 1) {
      setCurrentStep(currentStep + 1);
      setCardRevealed(false);  // Réinitialiser pour la prochaine carte
    } else {
      alert("Fin de la phase Donne/Prend !");
    }
  };

  return (
    <div>
      <h2>Phase Donne / Prend</h2>
      {/* Affiche une seule carte */}
      <div className="card">
        {steps[currentStep].label}
      </div>

      {/* Bouton pour révéler ou continuer */}
      {!cardRevealed ? (
        <button onClick={revealCard}>Révéler la carte</button>
      ) : (
        <button onClick={handleNext}>Continuer</button>
      )}
    </div>
  );
}

export default DonnePrendPhase;
