import React, { useState } from "react";
import Card from "../Card/Card";

function Round4({ player, onAction, deck }) {
  const [currentCard, setCurrentCard] = useState(null);
  const [guess, setGuess] = useState("");
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isCorrect, setIsCorrect] = useState(null);

  const handleSubmit = () => {
    if (deck.length === 0) {
      console.error("Le deck est vide, impossible de tirer une carte.");
      return; // Si le deck est vide, on sort de la fonction
    }

    const card = deck.pop(); // Tire une nouvelle carte du deck
    setCurrentCard(card);

    const correct = card.suit === guess;
    setIsCorrect(correct);
    setIsSubmitted(true);
    onAction(correct ? "distribute" : "drink", 4); // 4 gorgées pour ce round
  };

  const handleNextPlayer = () => {
    setIsSubmitted(false);
    setGuess("");
    onAction("next");
  };

  return (
    <div className="round4">
      <h2>{player}, devine la couleur de la prochaine carte :</h2>
      <button disabled={isSubmitted} onClick={() => setGuess("hearts")}>
        Cœur
      </button>
      <button disabled={isSubmitted} onClick={() => setGuess("diamonds")}>
        Carreau
      </button>
      <button disabled={isSubmitted} onClick={() => setGuess("spades")}>
        Pique
      </button>
      <button disabled={isSubmitted} onClick={() => setGuess("clubs")}>
        Trèfle
      </button>

      {isSubmitted && currentCard && (
        <div>
          <Card card={currentCard} /> {/* Vérifie que currentCard est bien définie */}
          <p>
            La carte tirée est {currentCard.suit}.{" "}
            {isCorrect ? "Correct !" : "Incorrect !"}
          </p>
          <button onClick={handleNextPlayer}>Joueur suivant</button>
        </div>
      )}

      {!isSubmitted && (
        <button className="submit-btn" onClick={handleSubmit} disabled={!guess}>
          Valider
        </button>
      )}
    </div>
  );
}

export default Round4;
