import React, { useState } from "react";
import Card from "../Card/Card";

function Round3({ player, onAction, deck, previousCards }) {
  const [currentCard, setCurrentCard] = useState(null);
  const [guess, setGuess] = useState("");
  const [isSubmitted, setIsSubmitted] = useState(false);
  const [isCorrect, setIsCorrect] = useState(null);

  const [card1, card2] = previousCards;

  const handleSubmit = () => {
    const card = deck.pop(); // Tire une nouvelle carte
    setCurrentCard(card);

    const correct =
      (guess === "inside" &&
        ((card.value > card1.value && card.value < card2.value) ||
          (card.value > card2.value && card.value < card1.value))) ||
      (guess === "outside" &&
        (card.value < Math.min(card1.value, card2.value) ||
          card.value > Math.max(card1.value, card2.value)));

    setIsCorrect(correct);
    setIsSubmitted(true);
    onAction(correct ? "distribute" : "drink", 3); // 3 gorgées pour ce round
  };

  const handleNextPlayer = () => {
    setIsSubmitted(false);
    setGuess("");
    onAction("next");
  };

  return (
    <div className="round3">
      <h2>
        {player}, devine si la prochaine carte est à l'intérieur ou à
        l'extérieur des valeurs des cartes précédentes :
      </h2>
      <button disabled={isSubmitted} onClick={() => setGuess("inside")}>
        Intérieur
      </button>
      <button disabled={isSubmitted} onClick={() => setGuess("outside")}>
        Extérieur
      </button>

      {isSubmitted && (
        <div>
          <Card card={currentCard} />
          <p>
            Les cartes précédentes étaient {card1.value} et {card2.value}. La
            nouvelle carte est {currentCard.value}.{" "}
            {isCorrect ? "Correct !" : "Incorrect !"}
          </p>
          <button onClick={handleNextPlayer}>Joueur suivant</button>
        </div>
      )}

      {!isSubmitted && (
        <button className="submit-btn" onClick={handleSubmit} disabled={!guess}>
          Valider
        </button>
      )}
    </div>
  );
}

export default Round3;
